import React from 'react';
import Theme from "../theme";
import Screensize from './Screensize';

function OddEvent(props){
    const screenSize= Screensize();
    console.log(Screensize.width);
    return (
        <div className="timeline__event " style ={screenSize.width > 786 ?{flexDirection : 'row-reverse'} : {flexDirection : 'column'}}>
		<div className="timeline__event__icon " style ={{color : Theme.secondary, background: Theme.secondaryLight}}>
		</div>
		<div className="timeline__event__date "  style ={{color : Theme.secondaryLight, background: Theme.secondary,borderRadius : "0 6px 6px 0"} }>
			{props.date}
		</div>
		<div className="timeline__event__content ">
			<div className="timeline__event__title"  style ={{color : Theme.secondary}} >
				{props.title}
			</div>
			<div className="timeline__event__description">
				<p>{props.description}</p>
			</div>
		</div>
	</div>
    )
}

export default OddEvent;
