import React from 'react';
import Theme from "../theme";
import Screensize from './Screensize';

function OddEvent(props){
    const screenSize= Screensize();
	const CustomStyle={color : Theme.secondary, background: Theme.secondaryLight };
	const pseudoElementStyles = {
        '&::before': {
            // Add styles for the ::before pseudo-element here
            content: " ",
            width: "2px",
            height: "100%",
            background: "black",
            position: "absolute",
            top: "0%",
            left: "50%",
            transform: "translateX(-50%)",
        },
        '&::after': {
            // Add styles for the ::after pseudo-element here
            content: " ",
            width: "100%",
            height: "2px",
            background: "black",
            position: "absolute",
            left: "0%",
            top: "50%",
            transform: "translateY(-50%)",
        },
    };
    return (
        <div className="timeline__event  " style ={screenSize.width > 786 ?{flexDirection : 'row-reverse'} : {flexDirection : 'column'}}>
		<div className="timeline__event__icon " style={{ ...CustomStyle, ...pseudoElementStyles }}>
		</div>
		<div className="timeline__event__date "  style ={screenSize.width > 786 ?{color : Theme.secondaryLight, background: Theme.secondary,borderRadius : "0 6px 6px 0"}:{color : Theme.secondaryLight, background: Theme.secondary,borderRadius : "0"} }>
			{props.date}
		</div>
		<div className="timeline__event__content ">
			<div className="timeline__event__title"  style ={{color : Theme.secondary}} >
				{props.title}
			</div>
			<div className="timeline__event__description">
				<p>{props.description}</p>
			</div>
		</div>
	</div>
    )
}

export default OddEvent;
